import React from "react";
import Square from "./Square";


function Board(){
    const winner="";
    const squares=[];
    const XisNext=false;
    const newGame=()=>{
        squares=Array(9).fill(null);
        XisNext=true;
        winner="";
    }
    React.useEffect(()=>{
        newGame();
    },[])
    return(
        <div>
            <main>
                <button onClick={newGame}>New game</button>
                {squares.map(x=>{
                    <Square />
                })}

            </main>
        </div>
    )
}

export default Board;
