import React from "react";
import Square from "./Square";


function Board(){
    let winner="";
    let squares=[];
    let XisNext=false;
    const newGame=()=>{
        squares=Array(9).fill(null);
        XisNext=true;
        winner="";
    }
    React.useEffect(()=>{
        newGame();
    },[])
    return(
        <div>
            <main>
                <button onClick={newGame}>New game</button>
                {squares.map(x=>
                    <h1>x</h1>
                    
                )}

            </main>
        </div>
    )
}

export default Board;
