import React from "react";
import Square from "./Square";


function Board(){
    const results=Array(9);
    const [squares,setSquares]=React.useState([]);
    const [XisNext,setXisNext]=React.useState(false);
    let winner="";
    const [reset,setReset]=React.useState(false);
    const newGame=()=>{
        setSquares(Array(9).fill(null));
        setXisNext(true);
        console.log("reseted");
       
    }
    const put=(i)=>{
        results.splice(i,1,XisNext?"X":"")
      console.log(results)
    }

    React.useEffect(()=>{
        newGame();
    },[reset]);

    const xx=()=>{
        setReset(!reset);
        setSquares([]);
        console.log(reset)
    }

    const Player=()=>{
       setXisNext(!XisNext);
       console.log(XisNext)
    }

    const calculateWinner=()=>{
        const lines = [
          [0, 1, 2],
          [3, 4, 5],
          [6, 7, 8],
          [0, 3, 6],
          [1, 4, 7],
          [2, 5, 8],
          [0, 4, 8],
          [2, 4, 6]
        ];
        for (let i = 0; i < lines.length; i++) {
          const [a, b, c] = lines[i];
          if (
            results[a] &&
            results[a] === this.squares[b] &&
            results[a] === this.squares[c]
          ) {
            return results[a];
          }
        }
        return null;
      }

    return(
        <div>
            <button onClick={()=>xx()}>New game</button>
            <h1>{winner}</h1>
            <main>
                {squares.map((x,i)=>
                   <Square index={i} currentPlayer={XisNext?"X":"O"} changePlayer={Player} put={put}/>
                )}
            </main>
        </div>
    )
    
}


export default Board;
