import React from "react";
import Square from "./Square";


function Board(){
    const [squares,setSquares]=React.useState([]);
    const [XisNext,setXisNext]=React.useState(false);
    const [winner,setWinner]=React.useState();
    const [reset,setReset]=React.useState(false);
    const newGame=()=>{
        setSquares(Array(9).fill(null));
        setXisNext(true);
        console.log("reseted");
       
    }
    React.useEffect(()=>{
        newGame();
    },[reset]);

    const calculateWinner=()=> {
        const lines = [
          [0, 1, 2],
          [3, 4, 5],
          [6, 7, 8],
          [0, 3, 6],
          [1, 4, 7],
          [2, 5, 8],
          [0, 4, 8],
          [2, 4, 6]
        ];
        for (let i = 0; i < lines.length; i++) {
          const [a, b, c] = lines[i];
          if (
            this.squares[a] &&
            this.squares[a] === this.squares[b] &&
            this.squares[a] === this.squares[c]
          ) {
            return this.squares[a];
          }
        }
        return null;
      }
    const xx=()=>{
        setReset(!reset);
        setSquares([]);
        console.log(reset)
    }
    const Player=()=>{
       setXisNext(!XisNext);
       console.log(XisNext)
    }
    
    
    return(
        <div>
            <button onClick={()=>xx()}>New game</button>
            <main>
                {squares.map((x,i)=>
                    <Square index={i} currentPlayer={XisNext?"X":"O"} changePlayer={Player}/>
                )}
            </main>
        </div>
    )
}

export default Board;
