import Square from "./Square";


function Board(){
    let winner="";
    const [squares,setSquares]=React.useState();
    let XisNext=false;
    const newGame=()=>{
        sesquares=Array(9).fill(null);
        XisNext=true;
        winner="";
       
    }
    React.useEffect(()=>{
        newGame();
    },[]);

    
    return(
        <div>
            <main>
                <button onClick={newGame}>New game</button>
                
            </main>
        </div>
    )
}

export default Board;
