        const lines = [
          [0, 1, 2],
          [3, 4, 5],
          [6, 7, 8],
          [0, 3, 6],
          [1, 4, 7],
          [2, 5, 8],
          [0, 4, 8],
          [2, 4, 6]
        ];
        for (let i = 0; i < lines.length; i++) {
          const [a, b, c] = lines[i];
          if (
            results[a] &&
            results[a] === this.squares[b] &&
            results[a] === this.squares[c]
          ) {
            return results[a];
          }
        }
        return null;
      }

    return(
        <div>
        <h1>{winner}</h1>
        <div className="square">
            <button className="sqr" onClick={()=>PutValue(props.index)}>{value}</button>
        </div>
        </div>
    )
}

export default Square;
