import React from "react";
import Square from "./Square";


function Board(){
    const [squares,setSquares]=React.useState([]);
    const [XisNext,setXisNext]=React.useState(false);
    let winner="";
    const [reset,setReset]=React.useState(false);
    const newGame=()=>{
        setSquares(Array(9).fill(<Square></Square>));
        setXisNext(true);
        console.log("reseted");
       
    }
    React.useEffect(()=>{
        newGame();
    },[reset]);

    const xx=()=>{
        setReset(!reset);
        setSquares([]);
        console.log(reset)
    }
    const Player=()=>{
       setXisNext(!XisNext);
       console.log(XisNext)
    }
    return(
        <div>
            <button onClick={()=>xx()}>New game</button>
            <h1>{winner}</h1>
            <main>
                {squares.map((x,i)=>
                    
                    <Square index={i} currentPlayer={XisNext?"X":"O"} changePlayer={Player} />
                )}
            </main>
        </div>
    )
}

export default Board;
