import React from "react";
import Square from "./Square";


function Board(){
    let winner="";
    let squares=null;
    let XisNext=false;
    const newGame=()=>{
        squares=Array(9).fill(null);
        XisNext=true;
        winner="";
       
    }
    React.useEffect(()=>{
        newGame();
    });

    if(squares)
    return(
        <div>
            <main>
                <button onClick={newGame}>New game</button>
                

            </main>
        </div>
    )
}

export default Board;
